// Server Component (default)
export default function Profile() {
  return <div>{user.name}</div>
}
// Client Component
'use client'
export function Button() { return <button>Click</button> }

const Dashboard = dynamic(() => import('./Dashboard'), {
  loading: () => <Loading />
})

'use server'
export async function updateProfile(formData: FormData) {
  // Direct database access
  await db.users.update(formData)
}

import { test, expect } from '@playwright/test'
test('navigation works', async ({ page }) => {
  await page.goto('https://nextjs.org/')
  await expect(page).toHaveTitle(/Next.js/)
})

export middleware from './middleware.ts'
// middleware.ts
export function middleware(request: NextRequest) {
  const token = request.cookies.get('token')
  if (!token) return NextResponse.redirect('/login')
}

const memoizedValue = useMemo(() => {
  return computeExpensiveValue(a, b)
}, [a, b])

const ThemeContext = createContext()
export function ThemeProvider({ children }) {
  const [theme, setTheme] = useState('light')
  return (
    <ThemeContext.Provider value={{ theme, setTheme }}>
      {children}
    </ThemeContext.Provider>
  )
}

const Button = styled.button`
  background: blue;
  color: white;
  padding: 8px 16px;
`

import { render, screen } from '@testing-library/react'
test('renders button', () => {
  render(<Button>Click me</Button>)
  expect(screen.getByText('Click me')).toBeInTheDocument()
})

// Good: React escapes by default
<div>{userInput}</div>
// Bad: Only use with DOMPurify
<div dangerouslySetInnerHTML={{ __html: DOMPurify.sanitize(userInput) }} />

const fullName = computed(() => {
  return firstName.value + ' ' + lastName.value
})

import { test } from 'vitest'
import { mount } from '@vue/test-utils'
test('button click', () => {
  const wrapper = mount(Button)
  wrapper.find('button').trigger('click')
  expect(wrapper.emitted()).toHaveProperty('click')
})

// +page.server.js
export async function load({ fetch }) {
  const res = await fetch('https://api.example.com/data')
  return await res.json()
}

import { test } from '@playwright/test'
test('home page loads', async ({ page }) => {
  await page.goto('/')
  await expect(page).toHaveTitle(/Home/)
})

// Automatic in form actions
export const actions = {
  default: async ({ request }) => {
    const data = await request.formData()
    // CSRF already validated
  }
}

// app/routes/dashboard.tsx
export default function Dashboard() {
  return <Outlet />
}
// app/routes/dashboard.users.tsx
export default function Users() { ... }

// app/routes/api.health.ts
export async function loader() {
  return Response.json({ status: 'ok' })
}

export async function loader({ params }) {
  return json(await db.user.find(params.id))
}
export async function action({ request }) {
  await db.user.create(await request.formData())
}

export async function action({ request }) {
  const formData = await request.formData()
  const errors = validate(formData)
  if (errors) return badRequest({ errors })
}
